  const [price,setPrice]= useState(0);
const [venue,setVenue]= useState('')
const [pn,setpn] = useState('')
  const handleSubmit =(event)=>{
    console.log(changeFormat(eventDate))
    event.preventDefault()
  
  }
 const changeFormat=(date)=>{
  
  var originalValue = date
  var dateParts = originalValue.split('-');
  var formattedDate = dateParts[2] + '-' + dateParts[1] + '-' + dateParts[0];
 
 
  return formattedDate;
 }
  
  return (
    <div>
        <Navigation />
        <div className="createform">
        <form onSubmit={handleSubmit}>
          <div>
            <input type="text" placeholder='Event Name' value={eventName} onChange={(e)=>{seteventName(e.target.value)}}></input>
          </div>
          <div>
            <input type="text" placeholder='Ticket Price' value={price} onChange={(e)=>{setPrice(e.target.value)}}></input>
          </div>
          <div>
          <input type="file"
        accept="image/*" name="image" />
          </div>
          <div>
            <input type="date" value={eventDate} onChange={(e)=>{seteventDate(e.target.value)}}></input>
          </div>
          <div>
           <input type="text" placeholder="Venue" value={venue} onChange={(e)=>{setVenue(e.target.value)}}></input>
           </div>
          <div>
            <input type='text' placeholder='phone number' value={pn} onChange={(e)=>{setSelectionRange(e.target.value)}}></input>
          </div>
          
          <button>Create Event</button>
        </form>


        </div>
    </div>
  )
}

export default CreateEvent
