import React from 'react'
import { Navigation } from './Navigation'
import { useParams } from 'react-router-dom'
function MyCardDetails() {
   
    const {eventName,location,price,picture,capacity}= useParams()
    const image = atob(picture)

    async function addService(e){
            e.preventDefault()
    }
    async function addEnt(e){
        e.preventDefault()
    }

  return (
    <div><Navigation />
    <div class="content">
    <h2>Name: {eventName}</h2>
        <h2>Location: {location}</h2>
        <h1>Price : {price}</h1>
        <img src={image} alt="no valid image"></img>
        <p>Capacity : {capacity}</p>
        <div>
            <form onSubmit={addService}>
            <button style={{ marginRight: '10px' }}>Add Service</button>
            </form>
       <form>
         <button onSubmit={addEnt}>Add Entertainment</button>
       </form>
       
        </div>
        
    </div>
    </div>
  )
}

export default MyCardDetails
