import React,{useState,useEffect} from 'react'
import {Link,useNavigate} from 'react-router-dom'
import './loginform.css';

export const LoginForm = () => {
    const[username,setUsername] = useState('')
    const[password,setPassword] = useState('')
    const[errormsg,setErrormsg] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async () => {

        setErrormsg('');

        try {
          const response = await fetch('http://127.0.0.1:5000/auth', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'Access-Control-Allow-Origin': '*',
            },
            body: JSON.stringify({ username,password }),
          });

          const data = await response.json();

          if (response.ok) {
            if(data&&data.token)
            //setPassword(data.password);


            navigate('/Events')
          } else {
            //setPassword(''); // Reset password state on error or no data returned
            setErrormsg(data.message || 'Failed to retrieve password');
          }
        } catch (error) {
          setPassword(''); // Reset password state on error
          setErrormsg('Error occurred while fetching data idk why');
          console.error('Error weird:', error);
        }
      };
      const isAuthenticated = () => {
        const token = localStorage.getItem('token');
        return !!token;
      };

      if (isAuthenticated()) {
       navigate('/Events')
      }




    // var flag=false;

    // const creds = [['1','1'],['admin','admin'],['123','123'],['543','543']]
    // const handleSubmit=(e)=>{
    //     e.preventDefault()//prevents default behavior of submission

    //     for(var i = 0;i<creds.length;i++){
    //         if((creds[i][0]=== username) && (creds[i][1] === password)){
    //             navigate('/Events')
    //             flag=true
    //         }


    //     }
    // if(!flag)
    //     setErrormsg("Incorrect username or password")
    // }
  return (
    <section className="container forms">
    <div className="form login">
        <div className="form-content">
            <header>Login</header>
            <form onSubmit={handleSubmit}>
                <div className="field input-field">
                    <input 
                    type="text" 
                    placeholder="Username" 
                    className="input"
                    value={username}
                    onChange={(e) => {setUsername(e.target.value)}}
                    />

                </div>

                <div className="field input-field">
                    <input 
                    type="password" 
                    placeholder="Password" 
                    className="password" 
                    value={password}
                    onChange={(e) => {setPassword(e.target.value)}}
                    />
                    <i className='bx bx-hide eye-icon'></i>
                </div>
                {errormsg && <p style={{color:'red'}}>{errormsg}</p>}

                <div className="form-link">
                    Forgot password?
                </div>

                <div className="field button-field">
                    <button>Login</button>
                </div>
            </form>

            <div className="form-link">
                <Link to="/CreateAccount"><span>Don't have an account? </span></Link>
            </div>
        </div>








            </div>





</section>
  )
}
