import React,{useState,useEffect} from 'react'
import {Link,useNavigate} from 'react-router-dom'
import './loginform.css';

export const LoginForm = () => {
    const[username,setUsername] = useState('')
    const[password,setPassword] = useState('')
    const[errormsg,setErrormsg] = useState('');
    const navigate = useNavigate();
    const handleSubmit = async (e) => {
        e.preventDefault();
        setErrorMsg('');

        try {
          const response = await fetch('http://localhost:5000/get_password', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ username }),
          });

          const data = await response.json();

          if (response.ok) {
            setPassword(data.password);
            // Redirect to the /Events route after successful password retrieval
            history.push('/Events');
          } else {
            setPassword(''); // Reset password state on error or no data returned
            setErrorMsg(data.message || 'Failed to retrieve password');
          }
        } catch (error) {
          setPassword(''); // Reset password state on error
          setErrorMsg('Error occurred while fetching data');
          console.error('Error:', error);
        }
      };




