import React,{useState} from 'react'
import { Navigation } from './Navigation'
import './CreateEvent.css'

function CreateEvent() {
  
  const [eventName,seteventName] = useState("")
  const [eventDate,seteventDate] = useState("")
  const [price,setPrice]= useState("");

  const handleSubmit =()=>{
    changeFormat(eventDate)
    console.log(eventDate)
  
  }
 const changeFormat=(date)=>{
  var dateObject = new Date(date)
  var day = dateObject.getDate();
  var month = dateObject.getMonth() + 1; // January is 0, so we add 1
  var year = dateObject.getFullYear();

  // Format the date to dd-mm-yyyy
  var formattedDate = `${day}-${month < 10 ? '0' + month : month}-${year}`;
 }
  
  return (
    <div>
        <Navigation />
        <div className="createform">
        <form onSubmit={handleSubmit}>
          <div>
            <input type="text" placeholder='Event Name' value={eventName} onChange={(e)=>{seteventName(e.target.value)}}></input>
          </div>
          <div>
            <input type="text" placeholder='Ticket Price' value={price} onChange={(e)=>{setPrice(e.target.value)}}></input>
          </div>
          <div>
          <input type="file"
        accept="image/*" name="image" />
          </div>
          <div>
            <input type="date" value={eventDate} onChange={(e)=>{seteventDate(e.target.value)}}></input>
          </div>
         
          <button>Create Event</button>
          
        </form>


        </div>
    </div>
  )
}

export default CreateEvent
