import React from 'react'
import { Navigation } from './Navigation'
import { useParams } from 'react-router-dom'
import { useState } from 'react';
function MyCardDetails() {
    const [provider, setprovider] = useState();
    const [options, setoptions] = useState([]);
    const dropdownOptions = [
        { value: 'option1', label: 'Option 1' },
        { value: 'option2', label: 'Option 2' },
        { value: 'option3', label: 'Option 3' },
        // Add more options as needed
      ];

    useState(()=>{
        async function getServices(){
            const response = await(fetch('http://127.0.0.1:5000/getServices',{
                method: 'GET',
                headers:{
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                  },
            }))
            const data = await response.json()
            if(response.ok){
                console.log(data)
               const op =  data.map((opp)=>{
                    return {label:opp[0]}
               })
               console.log("op:",op)
               setoptions(op)
               console.log(options)
            }


        }
    getServices()},[])  
    const {eventName,location,price,picture,capacity}= useParams()
    const image = atob(picture)

    async function addServes(e){
            e.preventDefault()
            const response= await fetch()
    }
   

  return (
    <div><Navigation />
    <div className="content">
    <h2>Name: {eventName}</h2>
        <h2>Location: {location}</h2>
        <h1>Price : {price}</h1>
        <img src={image} alt="no valid image"></img>
        <p>Capacity : {capacity}</p>
        <button>Delete Event</button>
        <div>
            <form onSubmit={addServes}>
            <select id="dropdown" value={provider} onChange={(e)=>{setprovider(e.target.value)}}>
          <option value="">Please select</option>
          {options.map((option, index) => (
            <option key={index} >{option.label}</option>
          ))}
        </select>
            <button style={{ marginRight: '10px' }}>Add Service</button>
            </form>
       
       
        </div>
        
    </div>
    </div>
  )
}

export default MyCardDetails
